import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { LoginForm } from '../components/LoginForm';

export const LoginFormContainer = ({ users, handleLogin }) => {

    const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailPlaceholder, setEmailPlaceholder] = useState("Email...");
    const [passwordPlaceholder, setPasswordPlaceholder] = useState("Password...");
    const [emailIsEmpty, setEmailIsEmpty] = useState(false);
    const [passwordIsEmpty, setPasswordIsEmpty] = useState(false);

// Handle Submit

    const handleSubmit = (e) => {
        e.preventDefault();
        if (email === '') {
            emailEmptyError();
            const emailInput = document.getElementById('emailInput');
            emailInput.focus();
        }
        if (password === '') {
            passwordEmptyError();
            const passwordInput = document.getElementById('passwordInput');
            passwordInput.focus();
        }
        handleLogin(email, password);
        const correctLoginCredentials = users.find(
            (user) => user.email === email && user.password === password
        );
        if (correctLoginCredentials) {
            navigate('/mockmail'); // Use Navigate.push to navigate
        }
    };

// Email

    const handleEmailClick = () => {
        setEmailPlaceholder("");
    };
    
    const handleEmailBlur = () => {
        if (email === "") {
            setEmailPlaceholder("Email...");
        }
    };

    const emailEmptyError = () => {
        if (email === '') {
            setEmailIsEmpty(true);
        }
    }

// Password

    const handlePasswordClick = () => {
        setPasswordPlaceholder("");
    };

    
    const handlePasswordBlur = () => {
        if (password === "") {
            setPasswordPlaceholder("Password...");
        }
    };

    const passwordEmptyError = () => {
        if (password === '') {
            setpasswordIsEmpty(true);
        }
    }

// Create Account Button

    const handleCreateAccountClick = () => {
        navigate('/create-account');
    };

    return(
        <>
            <LoginForm
                email={email}
                setEmail={setEmail}
                password={password}
                setPassword={setPassword}
                handleSubmit={handleSubmit}
                handlePasswordClick={handlePasswordClick}
                handleEmailClick={handleEmailClick}
                emailPlaceholder={emailPlaceholder}
                passwordPlaceholder={passwordPlaceholder}
                handleEmailBlur={handleEmailBlur}
                handlePasswordBlur={handlePasswordBlur}
                handleCreateAccountClick={handleCreateAccountClick}
                emailIsEmpty={emailIsEmpty}
            />
        </>
    );
};
