import React, { useState, useEffect } from "react";
import { StaticElementComponent } from "../components/StaticElementComponent";
import googleWritingSvg from "../images/google-writing-svg.svg";
import styled, { keyframes } from "styled-components";

export const StaticElementContainer = ({ children }) => {
  const [isImageLoaded, setIsImageLoaded] = useState(false);

  useEffect(() => {
    const image = new Image();
    image.src = googleWritingSvg;
    image.onload = () => {
      setIsImageLoaded(true);
    };
  }, []);

  const LoaderContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 65vh;
    position: relative;
  `;

  const snakeAnimation = keyframes`
    0%, 100% {
      transform: scaleX(0);
      transform-origin: right;
      animation-timing-function: ease-in-out;
      animation-delay: 0s;
    }
    50% {
      transform: scaleX(1);
      transform-origin: left;
      animation-timing-function: ease-in-out;
      animation-delay: 0s;
    }
    60% {
      transform: scaleX(1);
      transform-origin: left;
      animation-timing-function: ease-out;
      animation-delay: 0.2s;
    }
    70% {
      transform: scaleX(1);
      transform-origin: left;
      animation-timing-function: ease-in;
      animation-delay: 0.2s;
    }
    80% {
      transform: scaleX(1);
      transform-origin: left;
      animation-timing-function: ease-out;
      animation-delay: 0.2s;
    }
    90% {
      transform: scaleX(1);
      transform-origin: left;
      animation-timing-function: ease-in-out;
      animation-delay: 0.2s;
    }
  `;

  const SnakeLoader = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    height: 4px; /* Adjust the height of the loader bar */
    width: 100%;
    background-color: #2196F3; /* Blue color */
    animation: ${snakeAnimation} 3s linear infinite;
  `;

  

  const LoadingSpinner = () => {
    return (
      <LoaderContainer>
        <SnakeLoader />
      </LoaderContainer>
    );
  };

  if (!isImageLoaded) {
    // Render loading state or placeholder if the image is not loaded yet
    return <div><LoadingSpinner /></div>;
  }

  // Render the StaticElementComponent with the children once the image is loaded
  return (
    <StaticElementComponent>
      {children}
    </StaticElementComponent>
  );
};
